export default {
  name: 'GeneratorConfig',
  data() {
    return {
      config: this.initConfig(),
      rules: {
        author: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        basePackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 50, message: this.$t('rules.noMoreThan50'), trigger: 'blur' }
        ],
        entityPackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        mapperPackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        mapperXmlPackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        servicePackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        serviceImplPackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        controllerPackage: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { max: 20, message: this.$t('rules.noMoreThan20'), trigger: 'blur' }
        ],
        trimValue: [
          { validator: (rule, value, callback) => {
            if (this.config.isTrim === '1' && !value.trim().length) {
              callback(this.$t('rules.require'))
            } else {
              callback()
            }
          }, trigger: 'blur' },
          { max: 10, message: this.$t('rules.noMoreThan10'), trigger: 'blur' }
        ]
      }
    }
  },
  mounted() {
    this.getConfig()
  },
  methods: {
    initConfig() {
      return {
        id: '',
        author: '',
        basePackage: '',
        entityPackage: '',
        mapperPackage: '',
        mapperXmlPackage: '',
        servicePackage: '',
        serviceImplPackage: '',
        controllerPackage: '',
        isTrim: '',
        trimValue: ''
      }
    },
    getConfig() {
      this.$get('system/generatorConfig').then((r) => {
        const data = r.data.data
        this.config = { ...data }
      })
    },
    submit() {
      this.$refs.form.validate((valid) => {
        if (valid) {
          this.$post('system/generatorConfig', { ...this.config }).then(() => {
            this.$message({
              message: this.$t('tips.updateSuccess'),
              type: 'success'
            })
          })
        } else {
          return false
        }
      })
    }
  }
}
