import Pagination from '@/components/Pagination'
import smsSendStatusApi from '@/api/SmsSendStatus.js'
import { converEnum } from '@/utils/utils'

export default {
  name: 'SmsSendStatusManage',
  components: { Pagination },
  filters: {
    statusFilter(status) {
      const map = {
        false: 'danger',
        true: 'success'
      }
      return map[status] || 'success'
    }
  },
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      dialog: {
        isVisible: false,
        type: 'add'
      },
      tableKey: 0,
      // total: 0,
      queryParams: {
        taskId: 0
      },
      sort: {},
      selection: [],
      // 以下已修改
      loading: false,
      tableData: {
        total: 0
      },
      pagination: {
        size: 10,
        current: 1
      }
    }
  },
  computed: {
    sendStatusFilters() {
      return converEnum(this.$store.state.common.enums.SendStatus)
    },
    isVisible: {
      get() {
        return this.dialogVisible
      },
      set() {
        // this.close()
        // this.reset()
      }
    }
  },
  mounted() {
    // this.fetch()
  },
  methods: {
    onSelectChange(selection) {
      this.selection = selection
    },
    search() {
      this.fetch({
        ...this.queryParams,
        ...this.sort
      })
    },
    setTaskId(taskId) {
      this.queryParams.taskId = taskId
      this.fetch({
        ...this.queryParams,
        ...this.sort
      })
    },
    reset() {
      const taskId = this.queryParams.taskId
      this.queryParams = { taskId: taskId }
      this.sort = {}
      this.$refs.table.clearSort()
      this.$refs.table.clearFilter()
      this.search()
    },
    clearSelections() {
      this.$refs.table.clearSelection()
    },
    fetch(params = {}) {
      this.loading = true
      params.size = this.pagination.size
      params.current = this.pagination.current
      smsSendStatusApi.page(params).then(response => {
        const res = response.data
        this.loading = false
        this.tableData = res.data
      })
    },
    sortChange(val) {
      this.sort.field = val.prop
      this.sort.order = val.order
      this.search()
    },
    filterChange(filters) {
      for (const key in filters) {
        this.queryParams[key] = filters[key][0]
      }
      this.search()
    }
  }
}
