.search-item {
  margin: 0px 0 10px;
}
ul {
  // overflow-y: auto;
  // padding-left: 0rem;
  // margin-top: 0rem;
  padding: 0;
  margin: 0;
  overflow: auto;
  zoom: 1;
  height: 500px;
  li {
    // list-style: none;
    // float: left;
    // width: 60px;
    // text-align: center;
    // cursor: pointer;
    // color: #555;
    // transition: color 0.3s ease-in-out, background-color 0.3s ease-in-out;
    // position: relative;
    // // margin: 3px 0;
    // border-radius: 4px;
    // background-color: #fff;
    // overflow: hidden;
    padding: 0;
    float: left;
    margin: 2px;
    width: 100px;
    text-align: center;
    list-style: none;
    cursor: pointer;
    color: #5c6b77;
    transition: all 0.2s ease;
    position: relative;
  }
  span.active {
    i {
      border-radius: 2px;
      border-color: #4a4a48;
      background-color: #4a4a48;
      color: #fff;
      transition: all 0.3s;
    }
  }
  i {
    font-size: 1.7rem;
    border: 1px solid #f1f1f1;
    padding: 0.2rem;
    margin: 0.3rem;
    cursor: pointer;
    &:hover {
      border-radius: 2px;
      border-color: #4a4a48;
      background-color: #4a4a48;
      color: #fff;
      transition: all 0.3s;
    }
  }
  li p {
    word-break: break-all;
    overflow: hidden;
    margin: 2px 0;
    text-overflow: ellipsis;
    white-space: nowrap;
  }
}
