import echarts from 'echarts'
import 'echarts/map/js/china.js'
export default {
  name: 'Dashboard',
  data() {
    return {}
  },
  computed: {},
  mounted: function() {
    this.chinaConfigure()
    this.aa()
    const myChart = echarts.init(document.getElementById('chart_example'))
    const option = {
      color: ['#f44'],
      tooltip: {
        trigger: 'axis',
        axisPointer: {
          type: 'shadow'
        }
      },
      xAxis: [
        {
          type: 'category',
          data: [
            '1月',
            '2月',
            '3月',
            '4月',
            '5月',
            '6月',
            '7月',
            '8月',
            '9月',
            '10月',
            '11月',
            '12月'
          ],
          axisTick: {
            alignWithLabel: true
          }
        }
      ],
      yAxis: [
        {
          type: 'value'
        }
      ],
      series: [
        {
          name: '每月花费',
          type: 'bar',
          barWidth: '60%',
          data: [995, 666, 444, 858, 654, 236, 645, 546, 846, 225, 547, 356]
        }
      ]
    }
    myChart.setOption(option)
    window.addEventListener('resize', function() {
      myChart.resize()
    })
  },

  created() {},
  methods: {
    chinaConfigure() {
      const myChart = echarts.init(document.getElementById('chart_example1'))
      window.onresize = myChart.resize
      myChart.setOption({
        // 进行相关配置
        backgroundColor: '#fff',
        tooltip: {}, // 鼠标移到图里面的浮动提示框
        dataRange: {
          show: false,
          min: 0,
          max: 1000,
          text: ['High', 'Low'],
          realtime: true,
          calculable: true,
          color: ['orangered', 'yellow', 'lightskyblue']
        },
        geo: {
          // 这个是重点配置区
          map: 'china', // 表示中国地图
          roam: true,
          label: {
            normal: {
              show: true, // 是否显示对应地名
              textStyle: {
                color: 'rgba(0,0,0,0.4)'
              }
            }
          },
          itemStyle: {
            normal: {
              borderColor: 'rgba(0, 0, 0, 0.2)'
            },
            emphasis: {
              areaColor: null,
              shadowOffsetX: 0,
              shadowOffsetY: 0,
              shadowBlur: 20,
              borderWidth: 0,
              shadowColor: 'rgba(0, 0, 0, 0.5)'
            }
          }
        },
        series: [
          {
            type: 'scatter',
            coordinateSystem: 'geo' // 对应上方配置
          },
          {
            name: '启动次数', // 浮动框的标题
            type: 'map',
            geoIndex: 0,
            data: [
              {
                name: '北京',
                value: 599
              },
              {
                name: '上海',
                value: 142
              },
              {
                name: '黑龙江',
                value: 44
              },
              {
                name: '深圳',
                value: 92
              },
              {
                name: '湖北',
                value: 810
              },
              {
                name: '四川',
                value: 453
              }
            ]
          }
        ]
      })
    },
    aa() {
      const myChart1 = echarts.init(document.getElementById('chart_example2'))
      window.onresize = myChart1.resize
      myChart1.setOption({
        title: {
          left: 'center'
        },
        // legend: {
        //   left: 'center',
        //   top: 'bottom',
        //   data: [
        //     '待出库',
        //     '未出库',
        //     '已出库',
        //     '已出库1',
        //     '已出库2',
        //     '已出库3',
        //     '已出库4'
        //   ]
        // },

        series: [
          {
            name: '半径模式',
            type: 'pie',
            radius: [20, 110],
            center: ['60%', '40%'],
            roseType: 'radius',
            label: {
              show: false
            },
            emphasis: {
              label: {
                show: true
              }
            },
            data: [
              { value: 7, name: '待出库' },
              { value: 6, name: '未出库' },
              { value: 5, name: '已出库' },
              { value: 4, name: '已出库1' },
              { value: 3, name: '已出库2' },
              { value: 2, name: '已出库3' },
              { value: 1, name: '已出库4' }
            ]
          }
        ]
      })
    }
  }
}
