import Pagination from '@/components/Pagination'
import { readUserAgent } from '@/utils/utils'
import optLogApi from '@/api/OptLog.js'

export default {
  name: 'OptLog',
  components: { Pagination },
  filters: {
    timeFilter(time) {
      if (time < 500) {
        return 'success'
      } else if (time < 1000) {
        return ''
      } else if (time < 1500) {
        return 'warning'
      } else {
        return 'danger'
      }
    }
  },
  data() {
    return {
      tableKey: 0,
      loading: false,
      queryParams: {
        type: null,
        httpMethod: null
      },
      sort: {},
      selection: [],
      tableData: {},
      pagination: {
        size: 10,
        current: 1
      },
      drawer: false,
      currentRow: {}
    }
  },
  computed: {
    typeFilters() {
      return this.conver(this.$store.state.common.enums.LogType)
    },
    httpMethodFilters() {
      return this.conver(this.$store.state.common.enums.HttpMethod)
    }
  },
  mounted() {
    this.fetch()
  },
  methods: {
    conver(obj) {
      const list = []
      if (obj) {
        for (let key in obj) {
          list.push({
            text: obj[key],
            value: key
          })
        }
      }
      return list
    },
    onSelectChange(selection) {
      this.selection = selection
    },
    exportExcel() {},
    // filterHttpMethod (value, row) {
    //   return row.httpMethod.code === value
    // },
    // filterStatus (value, row) {
    //   return row.type.code === value
    // },
    fetch(params = {}) {
      params.current = this.pagination.current
      params.size = this.pagination.size
      if (this.queryParams.timeRange) {
        params.startCreateTime = this.queryParams.timeRange[0]
        params.endCreateTime = this.queryParams.timeRange[1]
      }
      this.loading = true

      optLogApi.page(params).then(response => {
        const res = response.data
        this.loading = false
        this.tableData = res.data
      })
    },
    singleDelete(row) {
      this.$refs.table.toggleRowSelection(row, true)
      this.batchDelete()
    },
    batchDelete() {
      if (!this.selection.length) {
        this.$message({
          message: this.$t('tips.noDataSelected'),
          type: 'warning'
        })
        return
      }
      this.$confirm(this.$t('tips.confirmDelete'), this.$t('common.tips'), {
        confirmButtonText: this.$t('common.confirm'),
        cancelButtonText: this.$t('common.cancel'),
        type: 'warning'
      })
        .then(() => {
          const logIds = this.selection.map(item => item.id)
          this.delete(logIds)
        })
        .catch(() => {
          this.clearSelections()
        })
    },
    clearSelections() {
      this.$refs.table.clearSelection()
    },
    delete(logIds) {
      optLogApi.delete({ ids: logIds }).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: this.$t('tips.deleteSuccess'),
            type: 'success'
          })
        }
        this.search()
      })
    },
    search() {
      this.fetch({
        ...this.queryParams,
        ...this.sort
      })
    },
    reset() {
      this.queryParams = {}
      this.sort = {}
      this.$refs.table.clearSort()
      this.$refs.table.clearFilter()
      this.search()
    },
    transTime(time) {
      return `${time} ms`
    },
    sortChange(val) {
      this.sort.field = val.prop
      this.sort.order = val.order
      this.search()
    },
    filterChange(filters) {
      for (const key in filters) {
        this.queryParams[key] = filters[key][0]
      }
      this.search()
    },
    onView(row) {
      this.currentRow = row
      this.drawer = true
    },
    closeDrawer(done) {
      done()
      this.currentRow = {}
    },
    uaForamt(row) {
      let ua = readUserAgent(row.ua)
      return ua.terminal + '  |  ' + ua.browser
    }
  }
}
