.sidebarLogoFade-enter-active {
  transition: opacity 1.5s;
}

.sidebarLogoFade-enter,
.sidebarLogoFade-leave-to {
  opacity: 0;
}

.sidebar-logo-container {
  position: relative;
  width: 100%;
  height: 54px;
  line-height: 54px;
  // background: #393e46;
  background-image: linear-gradient(to right, #d72228, #ed9247);
  text-align: center;
  overflow: hidden;

  & .sidebar-logo-link {
    height: 100%;
    width: 100%;

    & .sidebar-logo {
      margin-right: 12px;
      display: inline-block;
      vertical-align: middle;
      margin-bottom: -2px;
    }

    & .sidebar-title {
      display: inline-block;
      margin: 0;
      color: #fff;
      font-weight: 500;
      line-height: 50px;
      font-size: 19px;
      vertical-align: middle;
      & span {
        font-size: 12px;
        letter-spacing: 1px;
      }
    }
  }

  &.collapse {
    .sidebar-logo {
      margin-right: 0;
    }
  }
}
