import Pagination from '@/components/Pagination'
import DictionaryItemEdit from './DictionaryItemEdit'
import dictionaryItemApi from '@/api/DictionaryItem.js'

export default {
  name: 'DictionaryItemManage',
  components: { Pagination, DictionaryItemEdit },
  filters: {
    statusFilter(status) {
      const map = {
        false: 'danger',
        true: 'success'
      }
      return map[status] || 'success'
    }
  },
  data() {
    return {
      dialog: {
        isVisible: false,
        type: 'add'
      },
      tableKey: 0,
      queryParams: {
        dictionaryId: -1
      },
      sort: {},
      selection: [],
      // 以下已修改
      loading: false,
      dictionary: {
        id: null,
        code: ''
      },
      tableData: {
        total: 0
      },
      pagination: {
        size: 10,
        current: 1
      }
    }
  },
  computed: {},
  watch: {},
  mounted() {
    this.fetch({
      ...this.queryParams,
      ...this.sort
    })
  },
  methods: {
    filterStatus(value, row) {
      return row.status === value
    },
    editClose() {
      this.dialog.isVisible = false
    },
    editSuccess() {
      debugger
      this.search()
    },
    onSelectChange(selection) {
      this.selection = selection
    },
    search() {
      this.fetch({
        ...this.queryParams,
        ...this.sort
      })
    },
    reset() {
      this.queryParams = { dictionaryId: this.dictionary.id }
      this.sort = {}
      this.$refs.table.clearSort()
      this.$refs.table.clearFilter()
      this.search()
    },
    exportExcel() {
      this.$message({
        message: '待完善',
        type: 'warning'
      })
    },
    singleDelete(row) {
      this.$refs.table.toggleRowSelection(row, true)
      this.batchDelete()
    },
    batchDelete() {
      if (!this.selection.length) {
        this.$message({
          message: this.$t('tips.noDataSelected'),
          type: 'warning'
        })
        return
      }
      this.$confirm(this.$t('tips.confirmDelete'), this.$t('common.tips'), {
        confirmButtonText: this.$t('common.confirm'),
        cancelButtonText: this.$t('common.cancel'),
        type: 'warning'
      })
        .then(() => {
          const ids = []
          this.selection.forEach(u => {
            ids.push(u.id)
          })
          this.delete(ids)
        })
        .catch(() => {
          this.clearSelections()
        })
    },
    clearSelections() {
      this.$refs.table.clearSelection()
    },
    delete(ids) {
      dictionaryItemApi.delete({ ids: ids }).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: this.$t('tips.deleteSuccess'),
            type: 'success'
          })
        }
        this.search()
      })
    },
    add() {
      this.dialog.type = 'add'
      this.dialog.isVisible = true
      this.$refs.edit.setDictionaryItem({
        id: null,
        status: true,
        dictionaryId: this.dictionary.id,
        dictionaryCode: this.dictionary.code
      })
    },
    edit(row) {
      this.$refs.edit.setDictionaryItem(row)
      this.dialog.type = 'edit'
      this.dialog.isVisible = true
    },
    fetch(params = {}) {
      this.loading = true

      params.size = this.pagination.size
      params.current = this.pagination.current
      // if (this.queryParams.timeRange) {
      //   params.startCreateTime = this.queryParams.timeRange[0]
      //   params.endCreateTime = this.queryParams.timeRange[1]
      // }
      dictionaryItemApi.page(params).then(response => {
        const res = response.data
        this.loading = false
        if (res.isError) {
          return
        }
        this.tableData = res.data
      })
    },
    sortChange(val) {
      this.sort.field = val.prop
      this.sort.order = val.order
      this.search()
    },
    dictionaryClick(dictionary) {
      this.queryParams.dictionaryId = dictionary.id
      this.dictionary = dictionary
      this.search()
    }
  }
}
