import Pagination from '@/components/Pagination'
import Result from './Result'
import { getToken } from '@/utils/auth'
import { getFileType } from '@/utils'
import NProgress from 'nprogress'
import 'nprogress/nprogress.css'

export default {
  name: 'ExImport',
  components: { Pagination, Result },
  data() {
    return {
      dialogVisible: false,
      tableKey: 0,
      loading: false,
      list: null,
      total: 0,
      pagination: {
        size: 10,
        num: 1
      },
      fileList: [],
      headers: {
        Authorization: `bearer ${getToken()}`
      },
      data: [],
      error: [],
      time: '0 ms'
    }
  },
  mounted() {
    this.fetch()
  },
  methods: {
    closeDialog() {
      this.dialogVisible = false
    },
    templateDownload() {
      this.$download('system/eximport/template', {}, 'excel_import_template.xlsx')
    },
    exportExcel() {
      this.$download('system/eximport/excel', {
        pageSize: this.pagination.size,
        pageNum: this.pagination.num
      }, `export_${new Date().getTime()}.xlsx`)
    },
    beforeUpload(file) {
      const type = getFileType(file.name)
      if (type !== 'xlsx') {
        this.$message({
          message: this.$t('tips.onlySupportXlsx'),
          type: 'error'
        })
        return false
      } else {
        return true
      }
    },
    uploadError() {
      this.$message({
        message: this.$t('tips.uploadFailed'),
        type: 'error'
      })
      NProgress.done()
    },
    uploadSuccess(response) {
      const data = response.data
      this.data = data.data
      this.error = data.error
      this.time = data.time
      NProgress.done()
      this.fetch()
      this.dialogVisible = true
    },
    uploadProgress() {
      NProgress.start()
    },
    fetch(params = {}) {
      this.loading = true
      params.pageSize = this.pagination.size
      params.pageNum = this.pagination.num
      this.$get('system/eximport', { ...params }).then((r) => {
        const data = r.data.data
        this.total = data.total
        this.list = data.rows
        this.loading = false
      })
    }
  }
}
